var arguments = {}
for argument in OS.get_cmdline_args():
    if argument.find("=") > -1:
        var key_value = argument.split("=")
        arguments[key_value[0].lstrip("--")] = key_value[1]
    else:
        # 没有参数的选项将出现在字典中，
        # 其值被设置为空字符串。
        arguments[argument.lstrip("--")] = ""

var arguments = new Godot.Collections.Dictionary();
foreach (var argument in OS.GetCmdlineArgs())
{
    if (argument.Find("=") > -1)
    {
        string[] keyValue = argument.Split("=");
        arguments[keyValue[0].LStrip("--")] = keyValue[1];
    }
    else
    {
        // 没有参数的选项将出现在字典中，
        // 其值被设置为空字符串。
        arguments[keyValue[0].LStrip("--")] = "";
    }
}

switch (OS.GetName())
{
    case "Windows":
        GD.Print("Windows");
        break;
    case "macOS":
        GD.Print("macOS");
        break;
    case "Linux":
    case "FreeBSD":
    case "NetBSD":
    case "OpenBSD":
    case "BSD":
        GD.Print("Linux/BSD");
        break;
    case "Android":
        GD.Print("Android");
        break;
    case "iOS":
        GD.Print("iOS");
        break;
    case "Web":
        GD.Print("Web");
        break;
}
