func animate():
    if tween:
        tween.kill() # Abort the previous animation.
    tween = create_tween()

private Tween _tween;

public void Animate()
{
    if (_tween != null)
        _tween.Kill(); // Abort the previous animation
    _tween = CreateTween();
}

