func animate():
    if tween:
        tween.kill() # 终止之前的补间动画。
    tween = create_tween()

private Tween _tween;

public void Animate()
{
    if (_tween != null)
        _tween.Kill(); // 终止之前的补间动画。
    _tween = CreateTween();
}

