enum State {STATE_IDLE, STATE_JUMP = 5, STATE_SHOOT}
# Is the same as: const State = {STATE_IDLE = 0, STATE_JUMP = 5, STATE_SHOOT = 6} # Access values with State.STATE_IDLE, etc.
func _ready(): # Access values with Name.KEY, prints '5' print(State.STATE_JUMP) # Use dictionary methods: # prints '["STATE_IDLE", "STATE_JUMP", "STATE_SHOOT"]' print(State.keys()) # prints '{ "STATE_IDLE": 0, "STATE_JUMP": 5, "STATE_SHOOT": 6 }' print(State) # prints '[0, 5, 6]' print(State.values())

func square(a): return a * a

func hello_world(): print("Hello World")

func empty_function(): pass

func map(arr: Array, function: Callable) -> Array:
    var result = []
    for item in arr:
        result.push_back(function.call(item))
    return result

func add1(value: int) -> int:
    return value + 1;

func _ready() -> void:
    var my_array = [1, 2, 3]
    var plus_one = map(my_array, add1)
    print(plus_one) # Prints `[2, 3, 4]`.

